"use client";

import React, { createContext, useContext, useState } from "react";
import type { IEvent, IUser } from "@/modules/components/calendar/interfaces";
import { TCalendarView, TEventColor } from "@/modules/components/calendar/types";
import { useLocalStorage } from "@/modules/components/calendar/hooks";

interface ICalendarContext {
    selectedDate: Date;
    view: TCalendarView;
    setView: (view: TCalendarView) => void;
    agendaModeGroupBy: "date" | "color";
    setAgendaModeGroupBy: (groupBy: "date" | "color") => void;
    use24HourFormat: boolean;
    toggleTimeFormat: () => void;
    setSelectedDate: (date: Date | undefined) => void;
    selectedUserId: IUser["id"] | "all";
    setSelectedUserId: (userId: IUser["id"] | "all") => void;
    badgeVariant: "dot" | "colored";
    setBadgeVariant: (variant: "dot" | "colored") => void;
    selectedColors: TEventColor[];
    filterEventsBySelectedColors: (colors: TEventColor) => void;
    filterEventsBySelectedUser: (userId: IUser["id"] | "all") => void;
    users: IUser[];
    events: IEvent[];
    addEvent: (event: IEvent) => void;
    updateEvent: (event: IEvent) => void;
    removeEvent: (eventId: number) => void;
    clearFilter: () => void;
}

interface CalendarSettings {
    badgeVariant: "dot" | "colored";
    view: TCalendarView;
    use24HourFormat: boolean;
    agendaModeGroupBy: "date" | "color";
}

const DEFAULT_SETTINGS: CalendarSettings = {
    badgeVariant: "colored",
    view: "day",
    use24HourFormat: true,
    agendaModeGroupBy: "date"
};

const CalendarContext = createContext({} as ICalendarContext);

export function CalendarProvider({
                                     children,
                                     users,
                                     events,
                                     badge = "colored",
                                     view = "day",
                                 }: {
    children: React.ReactNode;
    users: IUser[];
    events: IEvent[];
    view?: TCalendarView;
    badge?: "dot" | "colored";
}) {
    const [settings, setSettings] = useLocalStorage<CalendarSettings>("calendar-settings", {
        ...DEFAULT_SETTINGS,
        badgeVariant: badge,
        view: view
    });

    const [badgeVariant, setBadgeVariantState] = useState<"dot" | "colored">(settings.badgeVariant);
    const [currentView, setCurrentViewState] = useState<TCalendarView>(settings.view);
    const [use24HourFormat, setUse24HourFormatState] = useState<boolean>(settings.use24HourFormat);
    const [agendaModeGroupBy, setAgendaModeGroupByState] = useState<"date" | "color">(settings.agendaModeGroupBy);

    const [selectedDate, setSelectedDate] = useState(new Date());
    const [selectedUserId, setSelectedUserId] = useState<IUser["id"] | "all">("all");
    const [selectedColors, setSelectedColors] = useState<TEventColor[]>([]);

    const [allEvents, setAllEvents] = useState<IEvent[]>(events || []);
    const [filteredEvents, setFilteredEvents] = useState<IEvent[]>(events || []);

    const updateSettings = (newPartialSettings: Partial<CalendarSettings>) => {
        setSettings({
            ...settings,
            ...newPartialSettings
        });
    };

    const setBadgeVariant = (variant: "dot" | "colored") => {
        setBadgeVariantState(variant);
        updateSettings({ badgeVariant: variant });
    };

    const setView = (newView: TCalendarView) => {
        setCurrentViewState(newView);
        updateSettings({ view: newView });
    };

    const toggleTimeFormat = () => {
        const newValue = !use24HourFormat;
        setUse24HourFormatState(newValue);
        updateSettings({ use24HourFormat: newValue });
    };

    const setAgendaModeGroupBy = (groupBy: "date" | "color") => {
        setAgendaModeGroupByState(groupBy);
        updateSettings({ agendaModeGroupBy: groupBy });
    };

    const filterEventsBySelectedColors = (color: TEventColor) => {
        const isColorSelected = selectedColors.includes(color);
        const newColors = isColorSelected
            ? selectedColors.filter((c) => c !== color)
            : [...selectedColors, color];

        if (newColors.length > 0) {
            const filtered = allEvents.filter((event) => {
                const eventColor = event.color || "blue";
                return newColors.includes(eventColor);
            });
            setFilteredEvents(filtered);
        } else {
            setFilteredEvents(allEvents);
        }

        setSelectedColors(newColors);
    };

    const filterEventsBySelectedUser = (userId: IUser["id"] | "all") => {
        setSelectedUserId(userId);
        if (userId === "all") {
            setFilteredEvents(allEvents);
        } else {
            const filtered = allEvents.filter((event) => event.user.id === userId);
            setFilteredEvents(filtered);
        }
    };

    const handleSelectDate = (date: Date | undefined) => {
        if (!date) return;
        setSelectedDate(date);
    };

    const addEvent = (event: IEvent) => {
        setAllEvents((prev) => [...prev, event]);
        setFilteredEvents((prev) => [...prev, event]);
    };

    const updateEvent = (event: IEvent) => {
        const updated = {
            ...event,
            startDate: new Date(event.startDate).toISOString(),
            endDate: new Date(event.endDate).toISOString()
        };

        setAllEvents((prev) =>
            prev.map((e) => (e.id === event.id ? updated : e))
        );
        setFilteredEvents((prev) =>
            prev.map((e) => (e.id === event.id ? updated : e))
        );
    };

    const removeEvent = (eventId: number) => {
        setAllEvents((prev) => prev.filter((e) => e.id !== eventId));
        setFilteredEvents((prev) => prev.filter((e) => e.id !== eventId));
    };

    const clearFilter = () => {
        setFilteredEvents(allEvents);
        setSelectedColors([]);
        setSelectedUserId("all");
    };

    const value = {
        selectedDate,
        setSelectedDate: handleSelectDate,
        selectedUserId,
        setSelectedUserId,
        badgeVariant,
        setBadgeVariant,
        users,
        selectedColors,
        filterEventsBySelectedColors,
        filterEventsBySelectedUser,
        events: filteredEvents,
        view: currentView,
        use24HourFormat,
        toggleTimeFormat,
        setView,
        agendaModeGroupBy,
        setAgendaModeGroupBy,
        addEvent,
        updateEvent,
        removeEvent,
        clearFilter
    };

    return (
        <CalendarContext.Provider value={value}>
            {children}
        </CalendarContext.Provider>
    );
}

export function useCalendar(): ICalendarContext {
    const context = useContext(CalendarContext);
    if (!context)
        throw new Error("useCalendar must be used within a CalendarProvider.");
    return context;
}

"use client";

import React, {createContext, useContext, useState, ReactNode, useCallback, useEffect} from 'react';
import { IEvent } from '@/modules/components/calendar/interfaces';
import {toast} from "sonner";
import {useCalendar} from "@/modules/components/calendar/contexts/calendar-context";

interface DragDropContextType {
  draggedEvent: IEvent | null;
  isDragging: boolean;
  startDrag: (event: IEvent) => void;
  endDrag: () => void;
  handleEventDrop: (date: Date, hour?: number, minute?: number) => void;
  onEventDropped?: (event: IEvent, newStartDate: Date, newEndDate: Date) => void;
  setOnEventDropped: (callback: (event: IEvent, newStartDate: Date, newEndDate: Date) => void) => void;
}

const DragDropContext = createContext<DragDropContextType | undefined>(undefined);

export function DndProvider({ children }: { children: ReactNode }) {
  const { updateEvent } = useCalendar();
  const [draggedEvent, setDraggedEvent] = useState<IEvent | null>(null);
  const [isDragging, setIsDragging] = useState(false);
  const [onEventDropped, setOnEventDroppedCallback] = useState<
    ((event: IEvent, newStartDate: Date, newEndDate: Date) => void) | undefined
  >(undefined);

  const startDrag = (event: IEvent) => {
    setDraggedEvent(event);
    setIsDragging(true);
  };

  const endDrag = () => {
    setDraggedEvent(null);
    setIsDragging(false);
  };

  const handleEventDrop = (targetDate: Date, hour?: number, minute?: number) => {
    if (!draggedEvent || !onEventDropped) return;

    const originalStart = new Date(draggedEvent.startDate);
    const originalEnd = new Date(draggedEvent.endDate);
    const duration = originalEnd.getTime() - originalStart.getTime();

    const newStart = new Date(targetDate);
    if (hour !== undefined) {
      newStart.setHours(hour);
      newStart.setMinutes(minute || 0);
    } else {
      newStart.setHours(originalStart.getHours());
      newStart.setMinutes(originalStart.getMinutes());
    }

    // Create new end date based on the same duration
    const newEnd = new Date(newStart.getTime() + duration);

    // Check if the event is being dropped in the same position
    const isSamePosition =
        originalStart.getFullYear() === newStart.getFullYear() &&
        originalStart.getMonth() === newStart.getMonth() &&
        originalStart.getDate() === newStart.getDate() &&
        originalStart.getHours() === newStart.getHours() &&
        originalStart.getMinutes() === newStart.getMinutes();

    if (!isSamePosition) {
      onEventDropped(draggedEvent, newStart, newEnd);
    }

    endDrag();
  };

  const setOnEventDropped = (callback: (event: IEvent, newStartDate: Date, newEndDate: Date) => void) => {
    setOnEventDroppedCallback(() => callback);
  };

  const handleEventUpdate = useCallback((event: IEvent, newStartDate: Date, newEndDate: Date) => {
    try {
      const updatedEvent = {
        ...event,
        startDate: newStartDate.toISOString(),
        endDate: newEndDate.toISOString(),
      };

      updateEvent(updatedEvent);
      toast.success("Event updated successfully");
    } catch {
      toast.error("Failed to update event");
    }
  }, [updateEvent]);

  useEffect(() => {
    setOnEventDropped(handleEventUpdate);
  }, [setOnEventDropped, handleEventUpdate]);

  return (
    <DragDropContext.Provider
      value={{
        draggedEvent,
        isDragging,
        startDrag,
        endDrag,
        handleEventDrop,
        onEventDropped,
        setOnEventDropped,
      }}
    >
      {children}
    </DragDropContext.Provider>
  );
}

export function useDragDrop() {
  const context = useContext(DragDropContext);
  if (context === undefined) {
    throw new Error('useDragDrop must be used within a DragDropProvider');
  }
  return context;
}

import {useForm} from "react-hook-form";
import {format, addMinutes, set} from "date-fns";
import {zodResolver} from "@hookform/resolvers/zod";
import {ReactNode, useEffect, useMemo} from "react";
import {toast} from "sonner";

import {useDisclosure} from "@/modules/components/calendar/hooks";
import {useCalendar} from "@/modules/components/calendar/contexts/calendar-context";
import {eventSchema, TEventFormData} from "@/modules/components/calendar/schemas";
import {COLORS} from "@/modules/components/calendar/constants";
import {IEvent} from "@/modules/components/calendar/interfaces";

import {Form, FormControl, FormField, FormItem, FormLabel, FormMessage} from "@/components/ui/form";
import {Input} from "@/components/ui/input";
import {Select, SelectContent, SelectItem, SelectTrigger, SelectValue} from "@/components/ui/select";
import {
    Dialog,
    DialogClose,
    DialogContent,
    DialogTrigger,
    DialogDescription,
    DialogHeader,
    DialogTitle,
    DialogFooter
} from "@/components/ui/dialog";
import {Button} from "@/components/ui/button";
import {Textarea} from "@/components/ui/textarea";
import {DateTimePicker} from "@/components/ui/date-time-picker";

interface IProps {
    children: ReactNode;
    startDate?: Date;
    startTime?: { hour: number; minute: number };
    event?: IEvent;
}

export function AddEditEventDialog({children, startDate, startTime, event}: IProps) {
    const {isOpen, onClose, onToggle} = useDisclosure();
    const {addEvent, updateEvent} = useCalendar();
    const isEditing = !!event;

    const initialDates = useMemo(() => {
        if (!isEditing && !event) {
            if (!startDate) {
                const now = new Date();
                return { startDate: now, endDate: addMinutes(now, 30) };
            }
            const start = startTime
                ? set(new Date(startDate), {
                    hours: startTime.hour,
                    minutes: startTime.minute,
                    seconds: 0,
                })
                : new Date(startDate);
            const end = addMinutes(start, 30);
            return { startDate: start, endDate: end };
        }

        return {
            startDate: new Date(event.startDate),
            endDate: new Date(event.endDate),
        };
    }, [startDate, startTime, event, isEditing]);

    const form = useForm<TEventFormData>({
        resolver: zodResolver(eventSchema),
        defaultValues: {
            title: event?.title ?? "",
            description: event?.description ?? "",
            startDate: initialDates.startDate,
            endDate: initialDates.endDate,
            color: event?.color ?? "blue",
        },
    });

    useEffect(() => {
        form.reset({
            title: event?.title ?? "",
            description: event?.description ?? "",
            startDate: initialDates.startDate,
            endDate: initialDates.endDate,
            color: event?.color ?? "blue",
        });
    }, [event, initialDates, form.reset]);


    const onSubmit = (values: TEventFormData) => {
        try {
            const formattedEvent: IEvent = {
                ...values,
                startDate: format(values.startDate, "yyyy-MM-dd'T'HH:mm:ss"),
                endDate: format(values.endDate, "yyyy-MM-dd'T'HH:mm:ss"),
                id: isEditing ? event.id : Math.floor(Math.random() * 1000000),
                user: isEditing
                    ? event.user
                    : {
                        id: Math.floor(Math.random() * 1000000).toString(),
                        name: "Jeraidi Yassir",
                        picturePath: null,
                    },
                color: values.color,
            };

            if (isEditing) {
                updateEvent(formattedEvent);
                toast.success("Event updated successfully");
            } else {
                addEvent(formattedEvent);
                toast.success("Event created successfully");
            }

            onClose();
            form.reset();
        } catch (error) {
            console.error(`Error ${isEditing ? "editing" : "adding"} event:`, error);
            toast.error(`Failed to ${isEditing ? "edit" : "add"} event`);
        }
    };

    return (
        <Dialog open={isOpen} onOpenChange={onToggle} modal={false}>
            <DialogTrigger asChild>{children}</DialogTrigger>
            <DialogContent>
                <DialogHeader>
                    <DialogTitle>{isEditing ? "Edit Event" : "Add New Event"}</DialogTitle>
                    <DialogDescription>
                        {isEditing ? "Modify your existing event." : "Create a new event for your calendar."}
                    </DialogDescription>
                </DialogHeader>

                <Form {...form}>
                    <form id="event-form" onSubmit={form.handleSubmit(onSubmit)} className="grid gap-4 py-4">
                        <FormField
                            control={form.control}
                            name="title"
                            render={({field, fieldState}) => (
                                <FormItem>
                                    <FormLabel htmlFor="title" className="required">Title</FormLabel>
                                    <FormControl>
                                        <Input
                                            id="title"
                                            placeholder="Enter a title"
                                            {...field}
                                            className={fieldState.invalid ? "border-red-500" : ""}
                                        />
                                    </FormControl>
                                    <FormMessage/>
                                </FormItem>
                            )}
                        />
                        <FormField
                            control={form.control}
                            name="startDate"
                            render={({field}) => (
                                <DateTimePicker form={form} field={field}/>
                            )}
                        />
                        <FormField
                            control={form.control}
                            name="endDate"
                            render={({field}) => (
                                <DateTimePicker form={form} field={field}/>
                            )}
                        />
                        <FormField
                            control={form.control}
                            name="color"
                            render={({field, fieldState}) => (
                                <FormItem>
                                    <FormLabel className="required">Variant</FormLabel>
                                    <FormControl>
                                        <Select value={field.value} onValueChange={field.onChange}>
                                            <SelectTrigger
                                                className={`w-full ${fieldState.invalid ? "border-red-500" : ""}`}>
                                                <SelectValue placeholder="Select a variant"/>
                                            </SelectTrigger>
                                            <SelectContent>
                                                {COLORS.map((color) => (
                                                    <SelectItem value={color} key={color}>
                                                        <div className="flex items-center gap-2">
                                                            <div
                                                                className={`size-3.5 rounded-full bg-${color}-600 dark:bg-${color}-700`}/>
                                                            {color}
                                                        </div>
                                                    </SelectItem>
                                                ))}
                                            </SelectContent>
                                        </Select>
                                    </FormControl>
                                    <FormMessage/>
                                </FormItem>
                            )}
                        />
                        <FormField
                            control={form.control}
                            name="description"
                            render={({field, fieldState}) => (
                                <FormItem>
                                    <FormLabel className="required">Description</FormLabel>
                                    <FormControl>
                                        <Textarea
                                            {...field}
                                            placeholder="Enter a description"
                                            className={fieldState.invalid ? "border-red-500" : ""}
                                        />
                                    </FormControl>
                                    <FormMessage/>
                                </FormItem>
                            )}
                        />
                    </form>
                </Form>
                <DialogFooter>
                    <DialogClose asChild>
                        <Button type="button" variant="outline">Cancel</Button>
                    </DialogClose>
                    <Button form="event-form" type="submit">
                        {isEditing ? "Save Changes" : "Create Event"}
                    </Button>
                </DialogFooter>
            </DialogContent>
        </Dialog>
    );
}

import {useCalendar} from "@/modules/components/calendar/contexts/calendar-context";
import {toast} from "sonner";
import {Button} from "@/components/ui/button";
import {TrashIcon} from "lucide-react";
import {
    AlertDialog,
    AlertDialogAction, AlertDialogCancel,
    AlertDialogContent, AlertDialogDescription, AlertDialogFooter,
    AlertDialogHeader, AlertDialogTitle,
    AlertDialogTrigger
} from "@/components/ui/alert-dialog";

interface DeleteEventDialogProps {
    eventId: number;
}

export default function DeleteEventDialog(
    {
        eventId
    }: DeleteEventDialogProps
) {

    const {removeEvent} = useCalendar()

    const deleteEvent = () => {
        try {
            removeEvent(eventId);
            toast.success("Event deleted successfully.");
        } catch {
            toast.error("Error deleting event.");
        }
    }

    if (!eventId) {
        return null;
    }

    return (
        <AlertDialog>
            <AlertDialogTrigger asChild>
                <Button variant="destructive"
                >
                    <TrashIcon />
                    Delete
                </Button>
            </AlertDialogTrigger>
            <AlertDialogContent>
                <AlertDialogHeader>
                    <AlertDialogTitle>Are you absolutely sure?</AlertDialogTitle>
                    <AlertDialogDescription>
                        This action cannot be undone. This will permanently delete your
                        event and remove event data from our servers.
                    </AlertDialogDescription>
                </AlertDialogHeader>
                <AlertDialogFooter>
                    <AlertDialogCancel>Cancel</AlertDialogCancel>
                    <AlertDialogAction onClick={deleteEvent}>Continue</AlertDialogAction>
                </AlertDialogFooter>
            </AlertDialogContent>
        </AlertDialog>
    )
}

"use client";

import {format, parseISO} from "date-fns";
import {Calendar, Clock, Text, User} from "lucide-react";

import {
    Dialog,
    DialogContent,
    DialogHeader,
    DialogTitle,
    DialogTrigger,
    DialogClose,
} from "@/components/ui/dialog";
import {ScrollArea} from "@/components/ui/scroll-area";

import type {IEvent} from "@/modules/components/calendar/interfaces";
import {ReactNode} from "react";
import {AddEditEventDialog} from "@/modules/components/calendar/dialogs/add-edit-event-dialog";
import {Button} from "@/components/ui/button";
import {toast} from "sonner";
import {formatTime} from "@/modules/components/calendar/helpers";
import {useCalendar} from "@/modules/components/calendar/contexts/calendar-context";

interface IProps {
    event: IEvent;
    children: ReactNode;
}

export function EventDetailsDialog({event, children}: IProps) {
    const startDate = parseISO(event.startDate);
    const endDate = parseISO(event.endDate);
    const {use24HourFormat, removeEvent} = useCalendar()

    const deleteEvent = (eventId : number) => {
        try {
            removeEvent(eventId);
            toast.success("Event deleted successfully.");
        }catch {
            toast.error("Error deleting event.");
        }
    }

    return (
        <Dialog>
            <DialogTrigger asChild>{children}</DialogTrigger>
            <DialogContent>
                <DialogHeader>
                    <DialogTitle>{event.title}</DialogTitle>
                </DialogHeader>

                <ScrollArea className="max-h-[80vh]">
                    <div className="space-y-4 p-4">
                        <div className="flex items-start gap-2">
                            <User className="mt-1 size-4 shrink-0 text-muted-foreground"/>
                            <div>
                                <p className="text-sm font-medium">Responsible</p>
                                <p className="text-sm text-muted-foreground">
                                    {event.user.name}
                                </p>
                            </div>
                        </div>

                        <div className="flex items-start gap-2">
                            <Calendar className="mt-1 size-4 shrink-0 text-muted-foreground"/>
                            <div>
                                <p className="text-sm font-medium">Start Date</p>
                                <p className="text-sm text-muted-foreground">
                                    {format(startDate, "EEEE dd MMMM")}
                                    <span className="mx-1">
                                        at
                                    </span>
                                    {formatTime(parseISO(event.startDate), use24HourFormat)}
                                </p>
                            </div>
                        </div>

                        <div className="flex items-start gap-2">
                            <Clock className="mt-1 size-4 shrink-0 text-muted-foreground"/>
                            <div>
                                <p className="text-sm font-medium">End Date</p>
                                <p className="text-sm text-muted-foreground">
                                    {format(endDate, "EEEE dd MMMM")}
                                    <span className="mx-1">
                                        at
                                    </span>
                                    {formatTime(parseISO(event.endDate), use24HourFormat)}
                                </p>
                            </div>
                        </div>

                        <div className="flex items-start gap-2">
                            <Text className="mt-1 size-4 shrink-0 text-muted-foreground"/>
                            <div>
                                <p className="text-sm font-medium">Description</p>
                                <p className="text-sm text-muted-foreground">
                                    {event.description}
                                </p>
                            </div>
                        </div>
                    </div>
                </ScrollArea>
                <div className="flex justify-end gap-2">
                    <AddEditEventDialog event={event}>
                        <Button variant="outline">
                            Edit
                        </Button>
                    </AddEditEventDialog>
                    <Button variant="destructive" onClick={
                        () => {
                            deleteEvent(event.id);
                        }
                    }>
                        Delete
                    </Button>
                </div>
                <DialogClose/>
            </DialogContent>
        </Dialog>
    );
}

import {formatTime} from "@/modules/components/calendar/helpers";
import {
    ResponsiveModal,
    ResponsiveModalContent,
    ResponsiveModalTrigger,
    ResponsiveModalHeader,
    ResponsiveModalTitle
} from "@/components/ui/responsive-modal";
import {cn} from "@/lib/utils";

import {ReactNode} from "react";
import {IEvent} from "@/modules/components/calendar/interfaces";
import {dayCellVariants} from "@/modules/components/calendar/views/month-view/day-cell";
import {EventBullet} from "@/modules/components/calendar/views/month-view/event-bullet";
import {useCalendar} from "@/modules/components/calendar/contexts/calendar-context";
import {format} from "date-fns";

interface EventListDialogProps {
    date: Date;
    events: IEvent[];
    maxVisibleEvents?: number;
    children?: ReactNode;
}

export function EventListDialog({
                                    date,
                                    events,
                                    maxVisibleEvents = 3,
                                    children
                                }: EventListDialogProps) {
    const cellEvents = events;
    const hiddenEventsCount = Math.max(cellEvents.length - maxVisibleEvents, 0);
    const {badgeVariant, use24HourFormat} = useCalendar();

    const defaultTrigger = (
        <span className="cursor-pointer">
      <span className="sm:hidden">
        +{hiddenEventsCount}
      </span>
      <span className="hidden sm:inline py-0.5 px-2 my-1 rounded-xl border">
         {hiddenEventsCount}
          <span className="mx-1">more...</span>
      </span>
    </span>
    );

    return (
        <ResponsiveModal>
            <ResponsiveModalTrigger asChild>
                {children || defaultTrigger}
            </ResponsiveModalTrigger>
            <ResponsiveModalContent className="sm:max-w-[425px]">
                <ResponsiveModalHeader>
                    <ResponsiveModalTitle className='my-2'>
                        <div className="flex items-center gap-2">
                            <EventBullet color={cellEvents[0]?.color} className=""/>
                            <p className="text-sm font-medium">
                                Events on {
                                format(date, "EEEE, MMMM d, yyyy")
                            }
                            </p>
                        </div>
                    </ResponsiveModalTitle>
                </ResponsiveModalHeader>
                <div className="max-h-[60vh] overflow-y-auto space-y-2">
                    {
                        cellEvents.length > 0 ? (
                            cellEvents.map((event) => (
                                <div
                                    key={event.id}
                                    className={cn(
                                        "flex items-center gap-2 p-2 border rounded-md hover:bg-muted",
                                        {
                                            [dayCellVariants({color: event.color})]: badgeVariant === "colored",
                                        }
                                    )}
                                >
                                    <EventBullet color={event.color} className=""/>
                                    <div className="flex-1">
                                        <p className="text-sm font-medium">{event.title}</p>
                                        <p
                                            className={cn("text-xs", {
                                                "text-muted": badgeVariant === "colored",
                                                "text-muted-foreground": badgeVariant === "dot",
                                            })}
                                        >
                                            {formatTime(event.startDate, use24HourFormat)}
                                        </p>
                                    </div>
                                </div>
                            ))) : (
                            <p className="text-sm text-muted-foreground">
                                No events for this date.
                            </p>
                        )
                    }
                </div>
            </ResponsiveModalContent>
        </ResponsiveModal>
    );
}

import React, {ReactNode} from 'react';
import {motion} from 'framer-motion';
import {useDragDrop} from '@/modules/components/calendar/contexts/dnd-context';
import {IEvent} from '@/modules/components/calendar/interfaces';

interface DraggableEventProps {
    event: IEvent;
    children: ReactNode;
    className?: string;
}

export function DraggableEvent({event, children, className}: DraggableEventProps) {
    const {startDrag, endDrag, isDragging, draggedEvent} = useDragDrop();

    const isCurrentlyDragged = isDragging && draggedEvent?.id === event.id;

    const handleClick = (e: React.MouseEvent<HTMLDivElement>) => {
        e.stopPropagation();
    };

    return (
        <motion.div
            className={`${className || ''} ${isCurrentlyDragged ? 'opacity-50 cursor-grabbing' : 'cursor-grab'}`}
            draggable
            onClick={(e: React.MouseEvent<HTMLDivElement>) => handleClick(e)}
            onDragStart={(e) => {
                (e as DragEvent).dataTransfer!.setData('text/plain', event.id.toString());
                startDrag(event);
            }}
            onDragEnd={() => {
                endDrag();
            }}
        >
            {children}
        </motion.div>
    );
}

import React, { ReactNode } from 'react';
import { useDragDrop } from '@/modules/components/calendar/contexts/dnd-context';

interface DroppableAreaProps {
  date: Date;
  hour?: number;
  minute?: number;
  children: ReactNode;
  className?: string;
}

export function DroppableArea({ date, hour, minute, children, className }: DroppableAreaProps) {
  const { handleEventDrop, isDragging } = useDragDrop();

  return (
    <div
      className={`${className || ''} ${isDragging ? 'drop-target' : ''}`}
      onDragOver={(e) => {
        // Prevent default to allow drop
        e.preventDefault();
        e.currentTarget.classList.add('bg-primary/10');
      }}
      onDragLeave={(e) => {
        e.currentTarget.classList.remove('bg-primary/10');
      }}
      onDrop={(e) => {
        e.preventDefault();
        e.currentTarget.classList.remove('bg-primary/10');
        handleEventDrop(date, hour, minute);
      }}
    >
      {children}
    </div>
  );
}

'use client';

import React, { useState, useCallback, useMemo } from "react";
import { Resizable, ResizeCallback } from "re-resizable";
import {
    parseISO,
    addMinutes,
    differenceInMinutes,
    format,
    isAfter,
    isBefore,
} from "date-fns";
import { motion } from "framer-motion";

import { useCalendar } from "@/modules/components/calendar/contexts/calendar-context";
import { cn } from "@/lib/utils";

import type { IEvent } from "@/modules/components/calendar/interfaces";

interface ResizableEventBlockProps {
    event: IEvent;
    children: React.ReactNode;
    className?: string;
}

const PIXELS_PER_HOUR = 96;
const MINUTES_PER_PIXEL = 60 / PIXELS_PER_HOUR;
const MIN_DURATION = 15; // in minutes

export function ResizableEvent({
                                   event,
                                   children,
                                   className,
                               }: ResizableEventBlockProps) {
    const { updateEvent, use24HourFormat } = useCalendar();

    const [isResizing, setIsResizing] = useState(false);
    const [resizePreview, setResizePreview] = useState<{
        start: string;
        end: string;
    } | null>(null);

    const start = useMemo(() => parseISO(event.startDate), [event.startDate]);
    const end = useMemo(() => parseISO(event.endDate), [event.endDate]);
    const durationInMinutes = useMemo(() => differenceInMinutes(end, start), [start, end]);

    const resizeBoundaries = useMemo(() => {
        const dayStart = new Date(start);
        dayStart.setHours(0, 0, 0, 0);

        const dayEnd = new Date(start);
        dayEnd.setHours(23, 59, 59, 999);

        return { dayStart, dayEnd };
    }, [start]);

    const handleResizeStart = useCallback(() => {
        setIsResizing(true);
    }, []);

    const handleResize: ResizeCallback = useCallback(
        (_, direction, ref) => {
            const newHeight = parseInt(ref.style.height, 10);
            const newDuration = Math.max(
                MIN_DURATION,
                Math.round((newHeight + 8) * MINUTES_PER_PIXEL)
            );
            const delta = newDuration - durationInMinutes;

            let newStart = start;
            let newEnd = end;

            if (direction.includes("top")) {
                newStart = addMinutes(start, -delta);
            } else if (direction.includes("bottom")) {
                newEnd = addMinutes(end, delta);
            }

            if (isBefore(newStart, resizeBoundaries.dayStart)) {
                newStart = resizeBoundaries.dayStart;
            }
            if (isAfter(newEnd, resizeBoundaries.dayEnd)) {
                newEnd = resizeBoundaries.dayEnd;
            }

            setResizePreview({
                start: format(newStart, use24HourFormat ? "HH:mm" : "h:mm a"),
                end: format(newEnd, use24HourFormat ? "HH:mm" : "h:mm a"),
            });

            updateEvent({
                ...event,
                startDate: newStart.toISOString(),
                endDate: newEnd.toISOString(),
            });
        },
        [start, end, durationInMinutes, resizeBoundaries, use24HourFormat, updateEvent, event]
    );

    const handleResizeStop = useCallback(() => {
        setIsResizing(false);
        setResizePreview(null);
    }, []);

    const resizeConfig = useMemo(() => ({
        minHeight: 15,
        maxHeight: 1440,
        enable: {
            top: true,
            bottom: true,
            topRight: false,
            bottomRight: false,
            bottomLeft: false,
            topLeft: false,
        },
        handleStyles: {
            top: {
                cursor: "ns-resize",
                height: "8px",
                top: "-4px",
                backgroundColor: "transparent",
            },
            bottom: {
                cursor: "ns-resize",
                height: "8px",
                bottom: "-4px",
                backgroundColor: "transparent",
            },
        },
        handleClasses: {
            top: "transition-colors rounded-sm",
            bottom: "transition-colors rounded-sm",
        },
        onResizeStart: handleResizeStart,
        onResize: handleResize,
        onResizeStop: handleResizeStop,
        className: cn(
            "transition-all duration-200",
            isResizing && "z-50 shadow-lg"
        ),
    }), [handleResizeStart, handleResize, handleResizeStop, isResizing]);

    return (
        <motion.div
            initial={{ opacity: 0, scale: 0.95 }}
            animate={{ opacity: 1, scale: 1 }}
            exit={{ opacity: 0, scale: 0.95 }}
            transition={{ duration: 0.2 }}
            className={cn("relative group", className)}
        >
            <Resizable {...resizeConfig}>
                {children}
            </Resizable>

            {isResizing && resizePreview && (
                <motion.div
                    initial={{ opacity: 0, y: -10 }}
                    animate={{ opacity: 1, y: 0 }}
                    exit={{ opacity: 0, y: -10 }}
                    className="absolute -top-8 left-1/2 transform -translate-x-1/2 bg-gray-900 text-white text-xs px-2 py-1 rounded shadow-lg z-50 whitespace-nowrap"
                >
                    {resizePreview.start} - {resizePreview.end}
                </motion.div>
            )}
        </motion.div>
    );
}

