import {cva} from "class-variance-authority";
import {endOfDay, isSameDay, parseISO, startOfDay} from "date-fns";

import type {VariantProps} from "class-variance-authority";
import {useCalendar} from "@/modules/components/calendar/contexts/calendar-context";
import {IEvent} from "@/modules/components/calendar/interfaces";
import {cn} from "@/lib/utils";
import {EventDetailsDialog} from "@/modules/components/calendar/dialogs/event-details-dialog";
import {DraggableEvent} from "@/modules/components/calendar/dnd/draggable-event";
import {formatTime} from "@/modules/components/calendar/helpers";

const eventBadgeVariants = cva(
    "mx-1 flex size-auto h-6.5 select-none items-center justify-between gap-1.5 truncate whitespace-nowrap rounded-md border px-2 text-xs",
    {
        variants: {
            color: {
                // Colored variants
                blue: "border-blue-200 bg-blue-50 text-blue-700 dark:border-blue-800 dark:bg-blue-950 dark:text-blue-300",
                green: "border-green-200 bg-green-50 text-green-700 dark:border-green-800 dark:bg-green-950 dark:text-green-300",
                red: "border-red-200 bg-red-50 text-red-700 dark:border-red-800 dark:bg-red-950 dark:text-red-300",
                yellow: "border-yellow-200 bg-yellow-50 text-yellow-700 dark:border-yellow-800 dark:bg-yellow-950 dark:text-yellow-300",
                purple: "border-purple-200 bg-purple-50 text-purple-700 dark:border-purple-800 dark:bg-purple-950 dark:text-purple-300",
                orange: "border-orange-200 bg-orange-50 text-orange-700 dark:border-orange-800 dark:bg-orange-950 dark:text-orange-300",

                // Dot variants
                "blue-dot": "bg-bg-secondary text-t-primary [&_svg]:fill-blue-600",
                "green-dot": "bg-bg-secondary text-t-primary [&_svg]:fill-green-600",
                "red-dot": "bg-bg-secondary text-t-primary [&_svg]:fill-red-600",
                "orange-dot": "bg-bg-secondary text-t-primary [&_svg]:fill-orange-600",
                "purple-dot": "bg-bg-secondary text-t-primary [&_svg]:fill-purple-600",
                "yellow-dot": "bg-bg-secondary text-t-primary [&_svg]:fill-yellow-600",
            },
            multiDayPosition: {
                first: "relative z-10 mr-0 w-[calc(100%_+_1px)] rounded-r-none border-r-0 [&>span]:mr-2.5",
                middle: "relative z-10 mx-0 w-[calc(100%_+_1px)] rounded-none border-x-0",
                last: "ml-0 rounded-l-none border-l-0",
                none: "",
            },
        },
        defaultVariants: {
            color: "blue-dot",
        },
    }
);

interface IProps extends Omit<VariantProps<typeof eventBadgeVariants>, "color" | "multiDayPosition"> {
    event: IEvent;
    cellDate: Date;
    eventCurrentDay?: number;
    eventTotalDays?: number;
    className?: string;
    position?: "first" | "middle" | "last" | "none";
}

export function MonthEventBadge({
                                    event,
                                    cellDate,
                                    eventCurrentDay,
                                    eventTotalDays,
                                    className,
                                    position: propPosition
                                }: IProps) {
    const {badgeVariant, use24HourFormat} = useCalendar();

    const itemStart = startOfDay(parseISO(event.startDate));
    const itemEnd = endOfDay(parseISO(event.endDate));

    if (cellDate < itemStart || cellDate > itemEnd) return null;

    let position: "first" | "middle" | "last" | "none" | undefined;

    if (propPosition) {
        position = propPosition;
    } else if (eventCurrentDay && eventTotalDays) {
        position = "none";
    } else if (isSameDay(itemStart, itemEnd)) {
        position = "none";
    } else if (isSameDay(cellDate, itemStart)) {
        position = "first";
    } else if (isSameDay(cellDate, itemEnd)) {
        position = "last";
    } else {
        position = "middle";
    }

    const renderBadgeText = ["first", "none"].includes(position);

    const color = (badgeVariant === "dot" ? `${event.color}-dot` : event.color) as VariantProps<typeof eventBadgeVariants>["color"];

    const eventBadgeClasses = cn(eventBadgeVariants({color, multiDayPosition: position, className}));

    return (
        <DraggableEvent event={event}>
            <EventDetailsDialog event={event}>
                <div role="button" tabIndex={0} className={eventBadgeClasses}>
                    <div className="flex items-center gap-1.5 truncate">
                        {!["middle", "last"].includes(position) && badgeVariant === "dot" && (
                            <svg width="8" height="8" viewBox="0 0 8 8" className="shrink-0">
                                <circle cx="4" cy="4" r="4"/>
                            </svg>
                        )}

                        {renderBadgeText && (
                            <p className="flex-1 truncate font-semibold">
                                {eventCurrentDay && (
                                    <span className="text-xs">
                  Day {eventCurrentDay} of {eventTotalDays} {" "}
                </span>
                                )}
                                {event.title}
                            </p>
                        )}
                    </div>

                    {renderBadgeText && <span>{formatTime(new Date(event.startDate), use24HourFormat)}</span>}
                </div>
            </EventDetailsDialog>
        </DraggableEvent>
    );
}

