                        <span className="flex-1 border-l py-2 text-center text-xs font-medium text-t-quaternary">
              {format(selectedDate, "EE")}{" "}
                            <span className="font-semibold text-t-secondary">{format(selectedDate, "d")}</span>
            </span>
                    </div>
                </div>

                <ScrollArea className="h-[800px]" type="always" ref={scrollAreaRef}>
                    <div className="flex">
                        {/* Hours column */}
                        <div className="relative w-18">
                            {hours.map((hour, index) => (
                                <div key={hour} className="relative" style={{height: "96px"}}>
                                    <div className="absolute -top-3 right-2 flex h-6 items-center">
                                        {index !== 0 && (
                                            <span className="text-xs text-t-quaternary">
                        {format(
                            new Date().setHours(hour, 0, 0, 0),
                            use24HourFormat ? "HH:00" : "h a"
                        )}
                      </span>
                                        )}
                                    </div>
                                </div>
                            ))}
                        </div>

                        {/* Day grid */}
                        <div className="relative flex-1 border-l">
                            <div className="relative">
                                {hours.map((hour, index) => (
                                    <div key={hour} className="relative" style={{height: "96px"}}>
                                        {index !== 0 && <div
                                            className="pointer-events-none absolute inset-x-0 top-0 border-b"></div>}

                                        <DroppableArea date={selectedDate} hour={hour} minute={0}
                                                       className="absolute inset-x-0 top-0 h-[48px]">
                                            <AddEditEventDialog startDate={selectedDate} startTime={{hour, minute: 0}}>
                                                <div
                                                    className="absolute inset-0 cursor-pointer transition-colors hover:bg-secondary"/>
                                            </AddEditEventDialog>
                                        </DroppableArea>

                                        <div
                                            className="pointer-events-none absolute inset-x-0 top-1/2 border-b border-dashed border-b-tertiary"></div>

                                        <DroppableArea date={selectedDate} hour={hour} minute={30}
                                                       className="absolute inset-x-0 bottom-0 h-[48px]">
                                            <AddEditEventDialog startDate={selectedDate} startTime={{hour, minute: 30}}>
                                                <div
                                                    className="absolute inset-0 cursor-pointer transition-colors hover:bg-secondary"/>
                                            </AddEditEventDialog>
                                        </DroppableArea>
                                    </div>
                                ))}

                                <RenderGroupedEvents groupedEvents={groupedEvents} day={selectedDate}/>
                            </div>

                            <CalendarTimeline/>
                        </div>
                    </div>
                </ScrollArea>
            </div>

            <div className="hidden w-72 divide-y border-l md:block">
                <DayPicker className="mx-auto w-fit" mode="single" selected={selectedDate}
                           onSelect={(date) => date && setSelectedDate(date)} initialFocus/>

                <div className="flex-1 space-y-3">
                    {currentEvents.length > 0 ? (
                        <div className="flex items-start gap-2 px-4 pt-4">
              <span className="relative mt-[5px] flex size-2.5">
                <span
                    className="absolute inline-flex size-full animate-ping rounded-full bg-success-400 opacity-75"></span>
                <span className="relative inline-flex size-2.5 rounded-full bg-success-600"></span>
              </span>

                            <p className="text-sm font-semibold text-t-secondary">Happening now</p>
