            <motion.div
                initial="initial"
                animate="animate"
                variants={staggerContainer}
                className="hidden sm:grid grid-cols-3 gap-4 flex-grow overflow-hidden auto-rows-fr lg:grid-cols-4 "
            >
                {MONTHS.map((month, monthIndex) => {
